package Encode

import (
	"log"
	"testing"
)

func TestEncoding_convertToBinary(t *testing.T) {
	tests := []struct {
		name     string
		e        *Encoding
		expected string
	}{
		{name: "empty_string", e: &Encoding{Input: ""}, expected: ""},
		{name: "regular_string", e: &Encoding{Input: "foo"}, expected: "011001100110111101101111"},
		{name: "word_with_digits", e: &Encoding{Input: "foo1234"}, expected: "01100110011011110110111100110001001100100011001100110100"},
		{name: "word_with_symbols", e: &Encoding{Input: "foo+/@"}, expected: "011001100110111101101111001010110010111101000000"},
		{name: "two_words", e: &Encoding{Input: "foo bar"}, expected: "01100110011011110110111100100000011000100110000101110010"},
		{name: "two_words_with_digits", e: &Encoding{Input: "foo12 bar34"}, expected: "0110011001101111011011110011000100110010001000000110001001100001011100100011001100110100"},
		{name: "paragraph", e: &Encoding{Input: "Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum."}, expected: ""},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			tt.e.convertStringToBinary()
		})
		if tt.e.binaryString != tt.expected {
			log.Fatalf("\nexpected:%s\ngot:\t %s", tt.expected, tt.e.binaryString)
		}
	}
}

func TestEncoding_Base64Encode(t *testing.T) {
	tests := []struct {
		name     string
		e        *Encoding
		expected string
	}{
		{name: "empty_string", e: &Encoding{Input: ""}, expected: ""},
		{name: "regular_string", e: &Encoding{Input: "foo"}, expected: "Zm9v"},
		{name: "word_with_digits", e: &Encoding{Input: "foo1234"}, expected: "Zm9vMTIzNA=="},
		{name: "word_with_symbols", e: &Encoding{Input: "foo+/@"}, expected: "Zm9vKy9A"},
		{name: "two_words", e: &Encoding{Input: "foo bar"}, expected: "Zm9vIGJhcg=="},
		{name: "two_words_with_digits", e: &Encoding{Input: "foo12 bar34"}, expected: "Zm9vMTIgYmFyMzQ="},
		{name: "paragraph", e: &Encoding{Input: "Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum."}, expected: ""},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			tt.e.Base64Encode()
		})
		if tt.e.Encoded != tt.expected {
			log.Fatalf("\nexpected: %s\nactual:\t  %s", tt.expected, tt.e.Encoded)
		}
	}
}
